new Swiper(".swiper-container",{slidesPerView:4,spaceBetween:40,navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"}});let headerBurger=document.querySelector(".header__burger"),headerMenu=document.querySelector(".header__ul"),body=document.querySelector("body");function burger(){headerMenu.classList.toggle("_active"),body.classList.toggle("_lock")}headerBurger.addEventListener("click",burger);let headerCover=document.querySelector(".header__cover");function windowsScroll(){pageYOffset>90?headerCover.classList.add("_scroll"):headerCover.classList.remove("_scroll")}window.addEventListener("scroll",windowsScroll);